targets.expr:
avg_over_time(
  node_hwmon_temp_celsius{service="pve-100"}[5m]
)
* on (chip, sensor, instance)
group_right(label)
node_hwmon_sensor_label{label=~"Tctl|Package id 0", service="pve-100"}

targets.query:
from(bucket: v.defaultBucket)
  |> range(start: -20s)
  |> filter(fn: (r) =>
    r.host =~ /^${Host:regex}$/ and
    r._measurement == "cpu" and
    r._field == "usage_idle" and
    r.cpu == "cpu-total"
  )
  |> map(fn: (r) => ({
        r with
        _value: r._value * -1.0 + 100.0
      })
    )
  |> keep(columns: ["_value"])
  |> last()

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) =>
    r.host =~ /^${Host:regex}$/ and
    r._measurement == "cpu" and
    r._field == "usage_idle")
  |> map(fn: (r) => ({
        r with
        _value: r._value * -1.0 + 100.0
      })
    )
  |> aggregateWindow(every: 10s, fn: last)
  |> drop(columns: ["host"])

targets.expr:
avg_over_time(
  node_hwmon_temp_celsius{service="pve-300"}[5m]
)
* on (chip, sensor, instance)
group_right(label)
node_hwmon_sensor_label{label=~"Tctl|Package id 0", service="pve-300"}

targets.query:
from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => 
    r.host =~ /^${Host:regex}$/ and
    r.interface =~ /^${WAN:regex}$/ and 
    r._measurement == "net" and
    r._field =~ /bytes_recv|bytes_sent/
  )
  |> aggregateWindow(every: 10s, fn: last)
  |> derivative(unit:1s)
  |> map(fn: (r) => ({
      r with
      _value: r._value * 8.0
    })
  )
  |> map(fn: (r) => ({_value:r._value, _time:r._time, _field:r._field}))
